import glob
import os
from os import path

import pytest

from src.text_extractor import __save_locally
from src.text_extractor import extract_text

pdf_bae64_string = ""
docx_bae64_string = ""
encoded_text = "yohai, Ruby on rails, java"


@pytest.fixture(autouse=True)
def cleanup():
    yield
    files = glob.glob('tmpFiles/*')
    for f in files:
        os.remove(f)


def test_save_locally_text_create():
    __save_locally(encoded_text, "test.txt")
    assert path.exists("tmpFiles/test.txt")


def test_save_locally_pdf_create():
    __save_locally(pdf_bae64_string, "test.pdf")
    assert path.exists("tmpFiles/test.pdf")


def test_save_locally_docx_create():
    __save_locally(docx_bae64_string, "test.pdf")
    assert path.exists("tmpFiles/test.pdf")


def test_extract_text_pdf():
    res = extract_text(pdf_bae64_string, "test.pdf")
    assert res.strip() == encoded_text.strip()


def test_extract_text_docx():
    res = extract_text(docx_bae64_string, "test.docx")
    assert res.strip() == encoded_text.strip()


def test_extract_text_txt():
    res = extract_text(encoded_text, "test.txt")
    assert res.strip() == encoded_text.strip()
